int
acpi_build(int ncpu)
{
	int err;

	acpi_ncpu = ncpu;
	tb = paddr_guest2host(XHYVE_ACPI_BASE, XHYVE_ACPI_SIZE);
	if (tb == NULL) {
		return (EFAULT);
	}

	err = xh_vm_get_hpet_capabilities(&hpet_capabilities);
	if (err != 0) {
		return (err);
	}

	acpitbl_build_rdsp();
	acpitbl_build_rsdt();
	acpitbl_build_xsdt();
	acpitbl_build_madt();
	acpitbl_build_fadt();
	acpitbl_build_hpet();
	acpitbl_build_mcfg();
	acpitbl_build_facs();
	acpitbl_build_dsdt();

	return (0);
}
